function myFunction(){document.getElementById("myDropdown").classList.toggle("services-show")}window.onclick=function(s){if(!s.target.matches(".dropbtn")){var e,t=document.getElementsByClassName("dropdown-content");for(e=0;e<t.length;e++){var n=t[e];n.classList.contains("services-show")&&n.classList.remove("services-show")}}};


